pragma solidity ^0.7.0;

import "./IERC20.sol";
import "./IForwarder.sol";

contract TokenForwarder is IForwarder {

    address constant internal ETH_TOKEN = 0xEeeeeEeeeEeEeeEeEeEeeEEEeeeeEeeeeeeeEEeE;

    function forward(address payable _destination, address _token) external override {
        if (_token == ETH_TOKEN) {
            uint256 balance = address(this).balance;
            _destination.transfer(balance);
        } else {
            IERC20 tokenContract = IERC20(_token);
            uint256 balance = tokenContract.balanceOf(address(this));
            tokenContract.transfer(_destination, balance);
        }
    }

    function destruct(address payable _destination) external override {
        selfdestruct(_destination);
    }
}
